webhook.body:
{
  "request": {
    "method": "POST",
    "urlPath": "/api/v1/pow/validate"
  },
  "response": {
    "status": 200
  },
  "postServeActions": {
    "webhook": {
      "method": "POST",
      "url": "{{jsonPath originalRequest.body '$.url'}}/validated",
      "headers": {
        "Content-Type": "application/json"
      },
      "delay": {
        "type": "fixed",
        "milliseconds": 1000
      },
      "body": "{\"public_key\": \"{{jsonPath originalRequest.body '$.public_key'}}\", \"block_hash\": \"{{jsonPath originalRequest.body '$.block_hash'}}\", \"block_height\": {{jsonPath originalRequest.body '$.block_height'}}, \"nonces\": {{jsonPath originalRequest.body '$.nonces'}}, \"dist\": {{jsonPath originalRequest.body '$.dist'}}, \"received_dist\": {{jsonPath originalRequest.body '$.dist'}}, \"r_target\": 0.9, \"fraud_threshold\": 0.1, \"n_invalid\": 0, \"probability_honest\": 0.99, \"fraud_detected\": false }"
    }
  }
}
